})

// 组件卸载时清理事件监听器
onUnmounted(() => {
    window.removeEventListener('resize', updateVisibleSlides)
    window.removeEventListener('scroll', handleScroll)
    // stopAutoPlay()
    // stopBenefitsAutoPlay()
})

