/* Materials Carousel */
.materials-carousel {
    width: 100%;
    overflow: hidden;
}

.carousel-container {
    position: relative;
    width: 100%;
    /* 精确计算：2.5个可视item + 2.5个gap + 左右padding */
    max-width: calc(420px * 2.5 + 32px * 2 + 20px * 2);
    /* 1134px */
    margin: 0 auto;
    padding: 10px;
    overflow: hidden;
}

.carousel-track {
    display: flex;
    gap: 32px;
    transition: transform 0.5s ease-in-out;
    width: calc(420px * 6 + 32px * 5);
    /* 6 items + 5 gaps */
}

.carousel-item {
    flex: 0 0 420px;
    height: 560px;
}

.material-card {
    width: 100%;
    height: 100%;
    background: #F0F4FF;
    border-radius: 24px;
    padding: 25px 10px 15px;
    display: flex;
    flex-direction: column;
    gap: 16px;
    overflow-y: auto;
}

.card-header {
    padding: 12px 20px 0;
    text-align: center;
}

.card-title {
    font-family: Inter, sans-serif;
    font-weight: 700;
    font-size: 24px;
    line-height: 1.21em;
    background: linear-gradient(90deg, rgba(71, 82, 230, 1) 29%, rgba(62, 128, 238, 1) 50%, rgba(36, 196, 255, 1) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin: 0;
}

.card-content {
