    padding: 20px 20px 20px;
    display: flex;
    flex-direction: row;
    gap: 8px;
}

/* Demo Video Styles */
.demo-video {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.video-placeholder {
    width: 100%;
    height: 284px;
    background: #F7F7F7;
    border: 1px solid #E8E8E8;
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
}

.play-button {
    cursor: pointer;
    transition: transform 0.3s ease;
}

.play-button:hover {
    transform: scale(1.1);
}

.video-description {
    font-family: Inter, sans-serif;
    font-weight: 300;
    font-size: 16px;
    line-height: 1.25em;
    color: #666666;
    text-align: center;
    margin: 0;
    padding: 10px;
    border: 1px solid transparent;
    border-top: 1px solid #E8E8E8;
}

/* Image Gallery Styles */
.image-gallery {
    display: flex;
    flex-direction: column;
    gap: 1px;
    border: 1px solid #E8E8E8;
    border-radius: 8px;
    overflow: hidden;
    margin-bottom: 8px;
}

.image-gallery img {
    width: 100%;
    height: auto;
    display: block;
}

.image-gallery img:first-child {
    height: 169px;
    object-fit: cover;
}

.image-gallery img:not(:first-child) {
    height: 169px;
    object-fit: cover;
    border-top: 1px solid #E8E8E8;
}

/* Featured Image */
.featured-image {
    width: 100%;
    height: 180px;
    object-fit: cover;
    border-radius: 8px;
    margin-bottom: 8px;
}

/* Card Description */
.card-description {
    flex: 1;
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.card-description p {
    font-family: Inter, sans-serif;
    font-weight: 300;
    font-size: 16px;
    line-height: 1.25em;
    color: #666666;
    margin: 0;
}

/* Carousel Controls */
.carousel-controls {
    position: absolute;
    top: 50%;
    left: -20px;
    right: -20px;
    transform: translateY(-50%);
    display: flex;
    justify-content: space-between;
    padding: 0 20px;
    pointer-events: none;
}

.carousel-btn {
    width: 40px;
    height: 40px;
    background: rgba(0, 0, 0, 0.2);
    border: none;
    border-radius: 50%;
    color: white;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
    pointer-events: all;
    visibility: hidden;

    &.is-show {
        visibility: visible;
    }
}

.carousel-btn:hover {
    background: rgba(0, 0, 0, 0.4);
    transform: scale(1.1);
}

.carousel-btn svg {
    width: 24px;
    height: 24px;
}

/* Expert Consultation */
.expert-consultation {
