}

/* Slideshow Track for horizontal scrolling */
.slideshow-track {
    width: 100%;
    height: 100%;
    display: flex;
    transition: transform 0.5s ease-in-out;
    border-radius: 12px;
}

.slide-item {
    width: 100%;
    height: 100%;
    flex-shrink: 0;
    display: flex;
    align-items: center;
    justify-content: center;

    img {
        width: 100%;
        height: 100%;
        object-fit: cover;
        border-radius: 12px;
    }
}

.slideshow-controls {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 20px;
    z-index: 10;
    pointer-events: none; // 让背景区域不响应点击
}

.nav-buttons {
    display: flex;
    justify-content: space-between;
    width: 100%;
    pointer-events: auto; // 让按钮可以点击
}

.nav-btn {
    width: 48px;
    height: 48px;
    border-radius: 50%;
    border: none;
    background: rgba(0, 0, 0, 0.3);
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
    color: #FFFFFF;

    &:hover:not(:disabled) {
        background: rgba(0, 0, 0, 0.3);
        transform: scale(1.1);
    }

    &:disabled {
        opacity: 0.3;
        cursor: not-allowed;
        background: rgba(0, 0, 0, 0.3);
    }

    svg {
        width: 24px;
        height: 24px;
    }
}

.pagination-dots {
    position: absolute;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    gap: 12px;
    background: rgba(0, 0, 0, 0.4);
    padding: 8px 16px;
    border-radius: 20px;
    pointer-events: auto; // 让分页点可以点击
}

.dot-btn {
    background: none;
    border: none;
    cursor: pointer;
    padding: 4px;
    transition: all 0.3s ease;

    &:hover .dot {
        background: rgba(255, 255, 255, 0.8);
        transform: scale(1.2);
    }

    .dot {
        width: 10px;
        height: 10px;
        border-radius: 50%;
        background: rgba(255, 255, 255, 0.5);
        transition: all 0.3s ease;
    }

    &.active .dot {
        background: #FFFFFF;
    }
}

.benefits-features {
