.solutions-carousel {
    width: 100%;
    max-width: 1193px;
    height: 620px;
    position: relative;
    overflow: hidden;
    padding: 10px;
}

.gradient-overlay {
    position: absolute;
    top: 0;
    bottom: 0;
    width: 90px;
    z-index: 5;
    pointer-events: none;
    opacity: 0;
    transition: opacity 0.3s ease;

    &.visible {
        opacity: 1;
    }

    &.left {
        left: 0;
        background: linear-gradient(to right, #F0F4FF 0%, rgba(240, 244, 255, 0) 100%);
    }

    &.right {
        right: 0;
        background: linear-gradient(to left, #F0F4FF 0%, rgba(240, 244, 255, 0) 100%);
    }
}

.solutions-track {
    display: flex;
    align-items: center;
    gap: 32px;
    height: 600px;
    transition: transform 0.5s ease;
    width: calc(410px * 3 + 32px * 2);
    /* 3 cards + 2 gaps */
}

.solution-card {
    width: 410px;
    height: 600px;
    display: flex;
    flex-direction: column;
    border-radius: 24px;
    overflow: hidden;
    flex-shrink: 0;
    transition: all 0.3s ease;
}

.solution-image {
    width: 410px;
    height: 230px;
