        font-family: Inter, sans-serif;
        font-weight: 800;
        font-size: 24px;
        line-height: 1.17em;
        color: #333333;
        margin: 0;
    }
}

.solution-description {
    width: 352px;
    display: flex;
    align-items: flex-start;

    p {
        font-family: Inter, sans-serif;
        font-weight: 300;
        font-size: 18px;
        line-height: 1.17em;
        color: #333333;
        margin: 0;
    }
}

.solution-cta {
    width: 352px;
    height: 36px;
    display: flex;
    align-items: center;
    background: linear-gradient(90deg, rgb(71, 82, 230) 29%, rgb(36, 196, 255) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;

    p {
        margin: 0;

        em {
            font-family: Inter, sans-serif;
            font-weight: 700;
            font-size: 18px;
            line-height: 1em;
            color: #000000;
            font-style: normal;
        }
    }
}

.solutions-navigation {
    position: absolute;
    bottom: 20px;
    left: 20px;
    right: 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    z-index: 10;
}

.solutions-dots {
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translate(0, -50%);
    display: flex;
    gap: 10px;
    padding: 10px;
    border-radius: 20px;
    background: rgba(0, 0, 0, 0.2);
}

.solution-dot {

    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.5);
    cursor: pointer;
    transition: all 0.3s ease;

    &.active {
        background: #FFFFFF;
    }

    &:hover {
        background: rgba(255, 255, 255, 0.8);
    }
}

.custom-solution-button {
    margin-top: 20px;

    .custom-solution-btn {
        width: 260px;
        height: 60px;
        background: #FFFFFF;
        border: none;
        border-radius: 100px;
